import styles from "../Drum/drum.module.css";

function Drum(data) {
  const play = () => {
    new Audio(data.sound).play();
  };

  return (
    <div classname="drum" onClick={play}>
      <img
        //Bild im Polaroid
        className={styles.image}
        src={`./assets/${data.img}`}
        alt=""
      />
    </div>
  );
}

export default Drum;
