import styles from "../Drum/drum.module.css";

function Drum(data) {
  // const play = () => {
  //   new Audio(data.sound).play();
  // onClick={play}
  // };

  if (data.type === "img") {
    return (
      <div className="drum">
        <div>
          <img
            //Icons
            className={styles.image}
            src={`../../../public/assets/icons/${data.src}`}
            alt=""
          />
        </div>
      </div>
    );
  } else {
    return null;
  }
}

export default Drum;
