import styles from "../Drum/drum.module.css";

function Drum(props) {
  const play = () => {
    new Audio(props.sound).play();
  };
  return (
    <div classname="drum">
      <div classname="icon" onClick={play}></div>
    </div>
  );
}

export default Drum;
