import styles from "../Drum/drum.module.css";

function Drum(data) {
  const play = () => {
    new Audio(data.sound).play();
  };

  return (
    <div classname="drum" onClick={play}>
      <img src={`../../../public/assets/icons/${data.img}`} alt="" />
    </div>
  );
}

export default Drum;
